mapper.select:

select title, logo, id 
from blog  
where id = #{id};

choose.when:

update blog 
   set title = #{title }
 where id = #{id }

choose.otherwise:

update blog 
   set title = #{title }, 
       logo = #{logo }       
 where id = #{id }

mapper.select:

select p.no, p.title, p.contents, p.reg_date as regDateTime, p.category_no as categoryNo 
  from post p, category c 
		where p.category_no = c.no
	  and p.category_no = #{categoryNo}
	  and c.blog_id = #{blogId}


select no,name,`desc` as description ,blog_id as blogId 
  from category 
 where category.name = #{categoryName}
   and blog_id = #{blogId}
