mapper.select:

select title, logo, id 
from blog  
where id = #{id};

choose.when:

update blog 
   set title = #{title }
 where id = #{id }

choose.otherwise:

update blog 
   set title = #{title }, 
       logo = #{logo }       
 where id = #{id }

mapper.select:

		select no, name, blog_Id as blogId, `desc` as desc
		  from category
 		 where blog_id = ${id };
